from mcpi.minecraft import Minecraftimport mcpi.block as blockimport pyautogui as pgimport numpy as npimport timeimport cv2pics = ['game.jpg','link.jpg','add.jpg']def scan(x):    pic=pg.screenshot()    img = cv2.cvtColor(np.array(pic),cv2.COLOR_RGB2BGR)    template = cv2.imread(x)    (h, w )= template.shape[:2]    res = cv2.matchTemplate(img,template,cv2.TM_CCOEFF_NORMED)    threshold = 0.75    loc = np.where( res >= threshold)    for pt in zip(*loc[::-1]):        pg.moveTo(pt[0] + w/2,pt[1] + h/2)        time.sleep(1)        pg.click()    cv2.waitKey(1000)def back():    pic=pg.screenshot()    img = cv2.cvtColor(np.array(pic),cv2.COLOR_RGB2BGR)    template = cv2.imread('back.jpg')    (h, w )= template.shape[:2]    res = cv2.matchTemplate(img,template,cv2.TM_CCOEFF_NORMED)    threshold = 0.75    loc = np.where( res >= threshold)    for pt in zip(*loc[::-1]):        pg.moveTo(pt[0] + w/2,pt[1] + h/2)        time.sleep(1)        pg.click()        return True    cv2.waitKey(1000)    mc=Minecraft.create()    pos=mc.player.getTilePos()    img=cv2.imread("round.png")    GrayImage=cv2.cvtColor(img,cv2.COLOR_BGR2GRAY)    ret,thresh1=cv2.threshold(GrayImage,200,255,cv2.THRESH_BINARY)    resized_image=~cv2.resize(thresh1,(32,32))    for x in range(32):        for y in range(32):            mc.setBlock(pos.x+32-x-1,pos.y-1,pos.z+y,35)    for x in range(32):        for y in range(32):            if (resized_image[x][y] != 0):                mc.setBlock(pos.x+32-x-1,pos.y-1,pos.z+y,35,15)                time.sleep(0.01)while True:    pic= pg.screenshot("my_screenshot.png",region=(0,520,850,510))    image = cv2.cvtColor(np.array(pic), cv2.COLOR_RGB2BGR)    for p in pics:        scan(p)    while back():        back()    cv2.imshow("screenshot",image)    cv2.waitKey(1000)    cv2.destroyAllWindows()